// check modal region by selector with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName: 'check modal region by selector with vg',
      baselineName: 'TestSimpleModal_VG',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check modal region by selector with vg', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/ModalsPage/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestSimpleModal_VG',
      {width: 700, height: 460}
    )
    await spec.click(driver, '#open_simple_modal')
    await eyes.check({region: '#simple_modal > .modal-content'})
    await eyes.close(undefined)
  })
})
