// check regions by coordinates in frame with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check regions by coordinates in frame with scroll stitching',
      hideScrollbars: false,
      stitchMode: 'Scroll',
      baselineName: 'TestCheckLongIFrameModal_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check regions by coordinates in frame with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckLongIFrameModal_Scroll',
      {width: 700, height: 460}
    )
    await spec.click(driver, {type: 'css', selector: '#stretched'})
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 0, width: 385, height: 5000},
      isFully: true,
    })
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 5000, width: 385, height: 5000},
      isFully: true,
    })
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 10000, width: 385, height: 5000},
      isFully: true,
    })
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 15000, width: 385, height: 5000},
      isFully: true,
    })
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 20000, width: 385, height: 5000},
      isFully: true,
    })
    await eyes.check({
      scrollRootElement: '#modal2',
      frames: [{type: 'css', selector: '#modal2 iframe'}],
      region: {left: 0, top: 25000, width: 385, height: 4072},
      isFully: true,
    })
    await eyes.close(undefined)
  })
})
