// check window fully on android chrome emulator on mobile page with horizontal scroll
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'Android 8.0 Chrome Emulator',
    })
    eyes = testSetup.getEyes({
      displayName:
        'check window fully on android chrome emulator on mobile page with horizontal scroll',
      baselineName: 'Android Emulator 8.0 Portrait scrolled_mobile fully',
      parentBranchName: 'default',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('check window fully on android chrome emulator on mobile page with horizontal scroll (@native @mobile @headfull)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/DynamicResolution/scrolled_mobile.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - iOS Safari Cropping',
      'Android Emulator 8.0 Portrait scrolled_mobile fully',
      undefined
    )
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
