// should hide and restore scrollbars with scroll stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should hide and restore scrollbars with scroll stitching',
      stitchMode: 'Scroll',
      baselineName: 'TestScrollbarsHiddenAndReturned_Fluent_Scroll',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should hide and restore scrollbars with scroll stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestScrollbarsHiddenAndReturned_Fluent_Scroll',
      {width: 700, height: 460}
    )
    await eyes.check({isFully: true})
    await eyes.check({
      region: '#inner-frame-div',
      frames: ['[name="frame1"]'],
      isFully: true,
    })
    await eyes.check({isFully: true})
    await eyes.close(undefined)
  })
})
