// should send dom when check region
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should send dom when check region',
      baselineName: 'Test SendDom',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send dom when check region', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/DomTest/dom_capture.html'
    )
    await eyes.open(driver, 'Test SendDom', 'Test SendDom', {
      width: 1000,
      height: 700,
    })
    await eyes.check({region: '#scroll1'})
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['hasDom'],
      true,
      undefined
    )
  })
})
